      return import('@/components/CaseCreate/SelectedItem');
    }
  },
  methods: {
    compactTitle: function compactTitle(name, name2) {
      return "".concat(name, " (").concat(name2, ")");
    },
    setIconFront: function setIconFront(id) {
      this.post.icon_front = id;
    },
    priceTotal: function priceTotal(id, price) {
      var mas = this.masPrice.id = price;
      console.log('masPrice: ' + mas);
