      this.post.usertax = val.label;
      console.log('usertax: ' + this.post.usertax);
      var str = String(val.label);
      str = str[0];
      console.log(str);
      this.userTaxNum = parseInt(str);
      var totalTax = this.pricePlus / 100 * this.userTaxNum;
      console.log('Tax ' + totalTax);
      this.userTaxAll = totalTax;
    },
    recordItem: function recordItem(value) {
      var _this = this;

      this.post.items = [];

      _.forEach(value, function (item) {
        _this.post.items.push({
          id: item.item_id,
          chance: 0
        });
      });
    },
    changeChance: function changeChance(id, value, price, char) {
      var a = 0;

      _.forEach(this.post.items, function (item) {
        if (item.id == id) {
          item.chance = value;
        }

        return a += item.chance;
      });

      var check = true;
      console.log('totalPrice: ' + this.totalPrice);

      if (char) {
        if (check) {
          this.totalPrice += parseFloat(price);
          Math.round(this.totalPrice * 100) / 100;
          check = !check;
          console.log('totalPrice: ' + this.totalPrice + 'price: ' + price + 'check:' + check);
        }
      } else {
        if (price != 0) {
          this.totalPrice -= Number(price);
        } else {
          this.calcTotalPrice();
        }
      }

      if (a <= 100) {
        this.totalOdds = a;
      } else {// alert('Odds most be low 100')
      }
    },
    changeChance22: function changeChance22() {
      var that = this;
      setTimeout(that.changeChance21, 500);
    },
