      var a = 0;

      _.forEach(this.post.items, function (item) {
        if (item.id == id) {
          item.chance = value;
        }

        return a += item.chance;
      });

      var element = document.querySelectorAll("#pricep");
      var elements = 0;
      var plus = 0;
      this.pricePlus = 0; //let triger = false
      //let sum

      for (var i = 0; i < element.length; i++) {
        console.log('55555' + element[i]);
        elements += element[i].innerHTML;
      }

      console.log('innerHTML2 ' + elements + '');
      elements = elements.split('$ ');

      for (var i = 0; elements.length > i; i++) {
        //возможно нужно будeт добавить /2
