        this.pricePlus = Math.round(this.pricePlus * 100) / 100;
      } //this.totalOdds2 = pricePlus
      //console.log('66666' + this.pricePlus + 'this.totalPrice: ' + totalPrice)
      //for (var i = Things.length - 1; i >= 0; i--) {
      //     Things[i]
      // } this.lastPrise


      if (char) {
        if (this.totalOdds <= 100) {
          this.totalPrice += Number(price);
          Math.round(this.totalPrice * 100) / 100; //this.totalPriseScrin = this.totalPrice + Number(price)

          console.log(this.totalPrice);
        }
      } else {
        if (price != 0) {
          this.totalPriseScrin = this.totalPrice - Number(price);
        } else {
          this.totalPriseScrin = this.calcTotalPrice();
        }
      }

      if (a <= 100) {
        this.totalOdds = a;
      } else {// alert('Odds most be low 100')
      }
    },
    searchItem: _.debounce(function () {
      var _this2 = this;

      document.getElementById('search').blur();
      this.showOverlay = true;
      var search = new Object();
      search['name'] = this.search;
      ApiCase.searchItems(Object.assign(search, this.sort)).then(function (response) {
        _this2.weaponsItem = response.data.data;
        setTimeout(function () {
          _this2.showOverlay = false;
        }, 500);
      }).catch(function (error) {
        console.log(error);
      });
    }, 700),
    deleteFromCase: function deleteFromCase(id) {
      this.checkboxItems = _.remove(this.checkboxItems, function (item) {
        return item.item_id != id;
      });
      this.post.items = _.remove(this.post.items, function (item) {
        return item.id != id;
      });
      this.calcTotalPrice();
    },
    calcTotalPrice: function calcTotalPrice() {
      var a = 0;

      _.forEach(this.checkboxItems, function (item) {
        return a += item.price;
      });

      this.totalPrice = Math.round(a * 100) / 100;
    },
    sortData: function sortData(type, item) {
      var _this3 = this;

      this.showOverlay = true;
      var direction = 'desc';
      var direction1 = 'desc';
      var search = new Object();
      search['name'] = this.search;

      if (this.sortToggle) {
        direction = 'asc';
      }

      if (this.sortToggle1) {
        direction1 = 'asc';
      }

      if (type == 'cost') {
        this.sortToggle = !this.sortToggle;
        this.sort['price_sort'] = direction;
      } else if (type == 'name') {
        this.sort['name_sort'] = direction1;
        this.sortToggle1 = !this.sortToggle1;
      } else if (type == 'rarity') {
        this.sort['rarity'] = item.value;
        this.rarityValue = item.name;
      } else if (type == 'weapon_type') {
        this.sort['weapon_type'] = item.value;
        this.typeWeaponValue = item.name;
      }

      ApiCase.filterItems(Object.assign(search, this.sort)).then(function (response) {
        console.log(response.data.data);

        if (response.data.result) {
          _this3.weaponsItem = response.data.data;
          setTimeout(function () {
            _this3.showOverlay = false;
          }, 500);
        }
      }).catch(function (error) {
        console.log(error);
      });
    },
    create: function create() {
      var _this4 = this;

      if (!this.disableBtn) {
        this.waitCreate = true;
        var temp = this.post.usertax.replace(' %', '');
        this.post.usertax = Number(temp) / 100;
        ApiCase.createCase(this.post).then(function (response) {
          _this4.waitCreate = false;
          console.log(response);
        });
      }
    },
    documentClick: function documentClick(e) {// let el = this.$refs.date
      // let target = e.target
      // if (( el !== target) && !el.contains(target)) {
      //     this.showSortByRate = false
      //     this.showSortByType = false
      // }
    }
  },
  beforeUpdate: function beforeUpdate() {
    if (this.$refs.slick) {
      this.$refs.slick.destroy();
    }
  },
  updated: function updated() {
    this.$nextTick(function () {
      if (this.$refs.slick) {
        this.$refs.slick.create(this.slickOptions);
      }
    });
  },
  destroyed: function destroyed() {
    document.removeEventListener('click', this.documentClick);
  },
  created: function created() {
    var _this5 = this;

    document.addEventListener('click', this.documentClick);
    ApiCase.getBackground().then(function (response) {
      if (response.data.result) {
        _this5.background = response.data.back_icons;
        setTimeout(function () {
          _this5.loadBack = false;
        }, 500);
      }

      console.log(response.data);
    }).catch(function (error) {
      console.log(error);
    });
    ApiCase.getItem().then(function (response) {
      if (response.data.result) {
        console.log(response.data.data);
        _this5.weaponsItem = response.data.data;
        setTimeout(function () {
          _this5.loadItem = false;
        }, 500);
        console.log(response.data);
      }
    }).catch(function (error) {
      console.log(error);
    });
  }
};
